function rainbowGradient(text) {
    // Create animated rainbow gradient effect (continuously looping)
    var style = 'display: inline-block; background: linear-gradient(90deg, #FF0000, #FF7F00, #FFFF00, #00FF00, #0000FF, #4B0082, #9400D3, #FF0000); background-size: 200% 100%; -webkit-background-clip: text; background-clip: text; -webkit-text-fill-color: transparent; animation: rainbowScroll 4s linear infinite; font-weight: bold;';
    return '<span style="' + style + '">' + text + '</span>';
}

function parseMarkdown(text) {
    // Bold: **text** or __text__
    text = text.replace(/\*\*(.*?)\*\*/g, '<b>$1</b>');
    text = text.replace(/__(.*?)__/g, '<b>$1</b>');
    
    // Italic: *text* or _text_
    text = text.replace(/\*(.*?)\*/g, '<i>$1</i>');
    text = text.replace(/_(.*?)_/g, '<i>$1</i>');
    
    // Underline: ~~text~~ (using strikethrough syntax but for underline)
    text = text.replace(/~~(.*?)~~/g, '<u>$1</u>');
    
    // Big: ++text++
    text = text.replace(/\+\+(.*?)\+\+/g, '<big>$1</big>');
    
    // Tiny: --(.*?)--
    text = text.replace(/--(.*?)--/g, '<small>$1</small>');
    
    // Color: {color|text} or [color]text[/color]
    text = text.replace(/\{([a-zA-Z]+)\|(.*?)\}/g, '<span style="color:$1;">$2</span>');
    text = text.replace(/\[([a-zA-Z]+)\](.*?)\[\/\1\]/g, '<span style="color:$1;">$2</span>');
    
    // Rainbow: !!text!! or [rainbow]text[/rainbow] - letter by letter rainbow
    text = text.replace(/!!(.*?)!!/g, function(match, content) {
        return rainbowGradient(content);
    });
    text = text.replace(/\[rainbow\](.*?)\[\/rainbow\]/g, function(match, content) {
        return rainbowGradient(content);
    });
    
    // Marquee: <<text>> or [marquee]text[/marquee]
    // Improved marquee with CSS animation
    text = text.replace(/<<(.*?)>>/g, function(match, content) {
        return '<span style="display: inline-block; overflow: hidden; white-space: nowrap; width: 100%; animation: marquee 10s linear infinite;"><span style="display: inline-block; padding-left: 100%; animation: marquee 10s linear infinite;">' + content + '</span></span>';
    });
    text = text.replace(/\[marquee\](.*?)\[\/marquee\]/g, function(match, content) {
        return '<span style="display: inline-block; overflow: hidden; white-space: nowrap; width: 100%; animation: marquee 10s linear infinite;"><span style="display: inline-block; padding-left: 100%; animation: marquee 10s linear infinite;">' + content + '</span></span>';
    });
    
    // Add CSS animations for marquee and rainbow if not already added
    if (!document.getElementById('marquee-style')) {
        var style = document.createElement('style');
        style.id = 'marquee-style';
        style.textContent = '@keyframes marquee { 0% { transform: translate(0, 0); } 100% { transform: translate(-100%, 0); } } @keyframes rainbowScroll { 0% { background-position: 0% 0%; } 100% { background-position: 200% 0%; } }';
        document.head.appendChild(style);
    }
    
    return text;
}
