function playBanSound() {
    setTimeout(function() {
        // Prefer Howler.js if available (better autoplay handling and unlock support)
        try {
            if (typeof Howl !== 'undefined') {
                if (!window.__banHowl) {
                    window.__banHowl = new Howl({
                        src: ['/sfx/ban.wav'],
                        preload: true,
                        volume: 1
                    });
                }
                window.__banHowl.play();
                return;
            }
        } catch (e) {
            console.log('Howler play error:', e);
        }

        // Fallback: Try to use preloaded audio element first
        var ban_sfx = document.getElementById("ban_sound_preload");
        if (ban_sfx) {
            ban_sfx.currentTime = 0;
            ban_sfx.volume = 1;
            ban_sfx.muted = false;
            ban_sfx.play().then(function() {
                // played
            }).catch(function(error) {
                console.log("Ban sound autoplay prevented on preloaded element:", error);
                // fallback: create a fresh Audio element and attach it to the DOM
                try {
                    var fb = new Audio("/sfx/ban.wav");
                    fb.volume = 1;
                    // append to DOM to ensure it's connected in all browsers
                    fb.style.display = 'none';
                    document.body && document.body.appendChild(fb);
                    fb.play().then(function(){
                        // success
                    }).catch(function(err2){
                        console.log("Ban sound fallback play failed:", err2);
                    });
                } catch (ex) {
                    console.log("Ban sound fallback creation error:", ex);
                }
            });
        } else {
            // Fallback to creating new audio element
            var ban_sfx_new = new Audio("/sfx/ban.wav");
            ban_sfx_new.volume = 1;
            ban_sfx_new.play().then(function(){}).catch(function(error){
                console.log("Ban sound fallback play failed:", error);
            });
        }
    }, 500);
}
